import React, { useState, useEffect, useCallback, useRef } from 'react';
import { initializeApp } from 'firebase/app';
import { getAuth, signInAnonymously, signInWithCustomToken, onAuthStateChanged, signOut } from 'firebase/auth';
import { getFirestore, collection, addDoc, getDocs, doc, updateDoc, deleteDoc, onSnapshot, query, where, setDoc } from 'firebase/firestore';
import { Home, CalendarPlus, MessageSquare, ListChecks, UserCircle, XCircle, Send, PlusCircle, Trash2, LogOut, CheckCircle, AlertTriangle, Info, Settings, Volume2, Eye, Moon, Sun, ZoomIn, ZoomOut, FileText, Activity, BookOpen, Search, MapPin, Menu, ChevronRight, ShieldCheck, FileBarChart2, Stethoscope, School } from 'lucide-react';
import './App.css';

// --- Firebase Configuration ---
// const firebaseConfig = {
//   apiKey: "YOUR_API_KEY",
//   authDomain: "YOUR_AUTH_DOMAIN",
//   projectId: "YOUR_PROJECT_ID",
//   storageBucket: "YOUR_STORAGE_BUCKET",
//   messagingSenderId: "YOUR_MESSAGING_SENDER_ID",
//   appId: "YOUR_APP_ID"
// };
const firebaseConfig = {
  apiKey: "AIzaSyC9yqipXCN_ALWZFrFUaBbMG4LznCgt4MM",
  authDomain: "medcare-app-9678b.firebaseapp.com",
  projectId: "medcare-app-9678b",
  storageBucket: "medcare-app-9678b.firebasestorage.app",
  messagingSenderId: "805339182842",
  appId: "1:805339182842:web:c4bab764c2c8b40527bb43",
  measurementId: "G-L8444GNX38"
};


// --- Global Firebase Variables (to be provided by the environment) ---
//const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
const appId = 'medcare-app-9678b';
// const finalFirebaseConfig = typeof __firebase_config !== 'undefined' ? JSON.parse(__firebase_config) : firebaseConfig;
//const finalFirebaseConfig ='false';

// --- Initialize Firebase ---
let app;
let auth;
let db;

try {
  //   app = initializeApp(finalFirebaseConfig);
  app = initializeApp(firebaseConfig);
  auth = getAuth(app);
  db = getFirestore(app);
  // import { setLogLevel } from "firebase/firestore"; // For debugging
  // setLogLevel('debug');
} catch (e) {
  console.error("Error initializing Firebase:", e);
  if (!db) {
    console.warn("Firestore (db) is not initialized. App will run with mock data and no persistence.");
  }
}

// Service worker

if ('serviceWorker' in navigator) {
  // Register a service worker hosted at the root of the
  // site using the default scope.
  navigator.serviceWorker.register('./service-worker.js').then(
    registration => {
      console.log('Service worker registration succeeded:', registration);
    },
    /*catch*/ error => {
      console.error(`Service worker registration failed: ${error}`);
    }
  );
} else {
  console.error('Service workers are not supported.');
}

// --- Mock Data ---
const MOCK_PRESCRIPTIONS = [
  { id: 'p1', name: 'Amoxicillin 250mg', dosage: '1 tablet every 8 hours for 7 days', date: '2024-05-20', doctor: 'Dr. Smith', notes: 'Complete full course.' },
{ id: 'p2', name: 'Ibuprofen 400mg', dosage: '1 tablet as needed for pain', date: '2024-05-15', doctor: 'Dr. Jones', notes: 'Max 3 tablets per day.' },
];

const MOCK_DOCTORS = [
  { id: 'doc1', name: 'Dr. Emily Carter (Cardiologist)' },
  { id: 'doc2', name: 'Dr. John Smith (General Practitioner)' },
  { id: 'doc3', name: 'Dr. Sarah Lee (Pediatrician)' },
];

const MOCK_LAB_RESULTS = [
  { id: 'lr1', testName: 'Complete Blood Count (CBC)', date: '2024-05-18', status: 'View Results', summary: 'All values within normal range.', doctor: 'Dr. Smith' },
  { id: 'lr2', testName: 'Lipid Panel', date: '2024-05-10', status: 'View Results', summary: 'Slightly elevated LDL cholesterol.', doctor: 'Dr. Carter' },
{ id: 'lr3', testName: 'Thyroid Stimulating Hormone (TSH)', date: '2024-04-20', status: 'Pending', summary: 'Results not yet available.', doctor: 'Dr. endocrinologist' },
];

const MOCK_MEDICAL_HISTORY = {
  diagnoses: [
    { id: 'd1', condition: 'Hypertension', date: '2022-01-15', notes: 'Managed with medication.' },
    { id: 'd2', condition: 'Seasonal Allergies', date: 'Ongoing', notes: 'Pollen, dust mites.' },
  ],
  allergies: [
    { id: 'a1', substance: 'Penicillin', reaction: 'Rash', severity: 'Mild' },
    { id: 'a2', substance: 'Peanuts', reaction: 'Anaphylaxis', severity: 'Severe' },
  ],
  immunizations: [
    { id: 'i1', vaccine: 'Influenza', date: '2023-10-01' },
    { id: 'i2', vaccine: 'Tetanus, Diphtheria, Pertussis (Tdap)', date: '2020-07-20' },
  ]
};

const MOCK_EDUCATIONAL_RESOURCES = [
  { id: 'er1', title: 'Understanding Your Blood Pressure', type: 'Article', category: 'Heart Health', summary: 'Learn about blood pressure readings and management.' },
{ id: 'er2', title: 'Managing Type 2 Diabetes', type: 'Video', category: 'Diabetes Care', summary: 'Tips for diet, exercise, and medication.' },
{ id: 'er3', title: 'Common Cold: Symptoms & Treatment', type: 'FAQ', category: 'General Wellness', summary: 'Frequently asked questions about the common cold.' },
];

const MOCK_CLINICS = [
  { id: 'c1', name: 'City General Hospital', address: '123 Main St, Anytown', type: 'Hospital', specialties: ['Cardiology', 'Oncology', 'Pediatrics'], insurance: ['BlueCross', 'Aetna'], rating: 4.5 },
{ id: 'c2', name: 'Downtown Urgent Care', address: '456 Oak Ave, Anytown', type: 'Urgent Care', specialties: ['General Medicine'], insurance: ['Cigna', 'UnitedHealthcare'], rating: 4.2 },
{ id: 'c3', name: 'Wellness Clinic', address: '789 Pine Ln, Anytown', type: 'Clinic', specialties: ['Family Medicine', 'Pediatrics'], insurance: ['BlueCross', 'Kaiser'], rating: 4.8 },
];


// --- Helper Components (Modal, Notification, SpeakButton, AccessibilitySettingsModal - unchanged, assuming they are robust) ---
// For brevity, these components are not repeated here but are assumed to be the same as in the previous version.
// Modal Component
const Modal = ({ isOpen, onClose, title, children, darkMode, highContrast, fontSizeClass }) => {
  if (!isOpen) return null;
  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
    <div className={`rounded-lg shadow-xl p-6 w-full max-w-md ${fontSizeClass} ${darkMode ? 'bg-gray-800 text-white' : 'bg-white text-gray-800'} ${highContrast ? (darkMode ? 'border-2 border-yellow-400' : 'border-2 border-blue-600') : ''}`}>
    <div className="flex justify-between items-center mb-4">
    <h3 className={`text-xl font-semibold ${highContrast && darkMode ? 'text-yellow-300' : highContrast ? 'text-blue-700' : (darkMode ? 'text-white' : 'text-gray-800')}`}>{title}</h3>
    <button onClick={onClose} className={`${darkMode ? 'text-gray-300 hover:text-white' : 'text-gray-500 hover:text-gray-700'}`}> <XCircle size={24} /> </button>
    </div>
    <div>{children}</div>
    </div>
    </div>
  );
};
const Notification = ({ message, type, onDismiss, darkMode }) => {
  if (!message) return null;
  let bgColor; if (darkMode) { bgColor = type === 'success' ? 'bg-green-700' : type === 'error' ? 'bg-red-700' : 'bg-blue-700'; } else { bgColor = type === 'success' ? 'bg-green-500' : type === 'error' ? 'bg-red-500' : 'bg-blue-500'; }
  const Icon = type === 'success' ? CheckCircle : type === 'error' ? AlertTriangle : Info;
  return ( <div className={`fixed top-4 right-4 ${bgColor} text-white p-4 rounded-lg shadow-md flex items-center z-[100]`}> <Icon size={24} className="mr-3" /> <span>{message}</span> {onDismiss && ( <button onClick={onDismiss} className="ml-4 text-white hover:text-gray-200"> <XCircle size={20} /> </button> )} </div> );
};
const SpeakButton = ({ textToSpeak, darkMode, highContrast }) => {
  const handleSpeak = (e) => { e.stopPropagation(); if ('speechSynthesis' in window && textToSpeak) { const utterance = new SpeechSynthesisUtterance(textToSpeak); window.speechSynthesis.cancel(); window.speechSynthesis.speak(utterance); } else { console.warn("Speech synthesis not supported or no text to speak."); } };
  return ( <button onClick={handleSpeak} aria-label={`Speak: ${textToSpeak.substring(0,30)}...`} title="Read aloud" className={`ml-2 p-1 rounded ${darkMode ? (highContrast ? 'bg-yellow-500 text-black' : 'bg-gray-600 hover:bg-gray-500') : (highContrast ? 'bg-blue-500 text-white' : 'bg-gray-200 hover:bg-gray-300') }`}> <Volume2 size={16} /> </button> );
};
const AccessibilitySettingsModal = ({ isOpen, onClose, settings, updateSettings, darkMode, highContrast, fontSizeClass }) => {
  const { fontSize, highContrast: hc, darkMode: dm, talkbackEnabled } = settings;
  const fontSizes = [ { label: 'Normal', value: 'text-base' }, { label: 'Large', value: 'text-lg' }, { label: 'Extra Large', value: 'text-xl' }, ];
  return ( <Modal isOpen={isOpen} onClose={onClose} title="Accessibility Settings" darkMode={darkMode} highContrast={highContrast} fontSizeClass={fontSizeClass}> <div className="space-y-6"> <div> <h4 className={`font-semibold mb-2 ${darkMode ? 'text-gray-200' : 'text-gray-700'}`}>Font Size</h4> <div className="flex space-x-2"> {fontSizes.map(fs => ( <button key={fs.value} onClick={() => updateSettings('fontSize', fs.value)} className={`px-3 py-1.5 rounded-md text-sm font-medium transition-colors ${fontSize === fs.value ? (darkMode ? 'bg-blue-500 text-white' : 'bg-blue-600 text-white') : (darkMode ? 'bg-gray-700 hover:bg-gray-600 text-gray-300' : 'bg-gray-200 hover:bg-gray-300 text-gray-700')} ${highContrast && fontSize === fs.value ? (darkMode ? 'border-2 border-yellow-400' : 'border-2 border-blue-800') : ''} ${highContrast && fontSize !== fs.value ? (darkMode ? 'border border-gray-500' : 'border border-gray-400') : ''} `}> {fs.label} </button> ))} </div> </div> <div> <h4 className={`font-semibold mb-2 ${darkMode ? 'text-gray-200' : 'text-gray-700'}`}>Theme</h4> <button onClick={() => updateSettings('darkMode', !dm)} className={`w-full flex items-center justify-center px-4 py-2 rounded-md text-sm font-medium transition-colors ${dm ? (highContrast ? 'bg-gray-900 text-yellow-300 border-2 border-yellow-400' : 'bg-gray-700 text-white') : (highContrast ? 'bg-white text-blue-700 border-2 border-blue-700' : 'bg-gray-200 text-black')} hover:${dm ? (highContrast ? 'bg-gray-800' : 'bg-gray-600') : (highContrast ? 'bg-gray-100' : 'bg-gray-300')} `}> {dm ? <Sun size={18} className="mr-2" /> : <Moon size={18} className="mr-2" />} {dm ? 'Switch to Light Mode' : 'Switch to Dark Mode'} </button> </div> <div> <h4 className={`font-semibold mb-2 ${darkMode ? 'text-gray-200' : 'text-gray-700'}`}>Contrast</h4> <button onClick={() => updateSettings('highContrast', !hc)} className={`w-full flex items-center justify-center px-4 py-2 rounded-md text-sm font-medium transition-colors ${hc ? (